operation.search:
	// Show lots of posts.
	elseif (!empty($context['latest_posts']))
	{
		echo '
			<dl id="ic_recentposts" class="middletext">';

		/* Each post in latest_posts has:
				board (with an id, name, and link.), topic (the topic's id.), poster (with id, name, and link.),
				subject, short_subject (shortened with...), time, link, and href. */
		foreach ($context['latest_posts'] as $post)
			echo '
				<dt><strong>', $post['link'], '</strong> ', $txt['by'], ' ', $post['poster']['link'], ' (', $post['board']['link'], ')</dt>
				<dd>', $post['time'], '</dd>';
		echo '
			</dl>';
	}
	echo '
		</div>';
}

operation.add:
// Show lots of posts.
elseif (!empty($context['latest_posts']))
{
	echo '
		<table id="ic_recentposts" class="middletext" width="100%">';

	/* Each post in latest_posts has:
			board (with an id, name, and link.), topic (the topic's id.), poster (with id, name, and link.),
			subject, short_subject (shortened with...), time, link, and href. */
	foreach ($context['latest_posts'] as $post)
		echo '
			<tr class="avalonic">
				<td width="45%"><img src="', $settings['theme_url'], '/images/post/xx.gif" alt="posts" /> <strong>', $post['link'], '</strong></td>
				<td width="35%"><img src="', $settings['theme_url'], '/images/icons/profile_sm.gif" alt="user" /> ', $post['poster']['link'], ' (', $post['board']['link'], ')</td>
				<td width="20%"><img src="', $settings['theme_url'], '/images/admin/scheduled.gif" alt="time" /> ', $post['time'], '</td>

					</tr>';
	echo '
		</table>';
}
echo '
	</div><br />';
	}

operation.search:
	// Events like community get-togethers.
	if (!empty($context['calendar_events']))
	{
		echo '
			<span class="event">', $context['calendar_only_today'] ? $txt['events'] : $txt['events_upcoming'], '</span> ';
		/* Each event in calendar_events should have:
				title, href, is_last, can_edit (are they allowed?), modify_href, and is_today. */
		foreach ($context['calendar_events'] as $event)
			echo '
				', $event['can_edit'] ? '<a href="' . $event['modify_href'] . '" title="' . $txt['calendar_edit'] . '"><img src="' . $settings['images_url'] . '/icons/modify_small.gif" alt="*" /></a> ' : '', $event['href'] == '' ? '' : '<a href="' . $event['href'] . '">', $event['is_today'] ? '<strong>' . $event['title'] . '</strong>' : $event['title'], $event['href'] == '' ? '' : '</a>', $event['is_last'] ? '<br />' : ', ';
	}
	echo '
		</p>';
}

operation.add:
	// Events like community get-togethers.
	if (!empty($context['calendar_events']))
	{
		echo '
			<span class="event">', $context['calendar_only_today'] ? $txt['events'] : $txt['events_upcoming'], '</span> ';
		/* Each event in calendar_events should have:
				title, href, is_last, can_edit (are they allowed?), modify_href, and is_today. */
		foreach ($context['calendar_events'] as $event)
			echo '
				', $event['can_edit'] ? '<a href="' . $event['modify_href'] . '" title="' . $txt['calendar_edit'] . '"><img src="' . $settings['images_url'] . '/icons/modify_small.gif" alt="*" /></a> ' : '', $event['href'] == '' ? '' : '<a href="' . $event['href'] . '">', $event['is_today'] ? '<strong>' . $event['title'] . '</strong>' : $event['title'], $event['href'] == '' ? '' : '</a>', $event['is_last'] ? '<br />' : ', ';
	}
	echo '
		</p>';
}

echo '<table width="100%"><tr>';

operation.search:
// Assuming there ARE users online... each user in users_online has an id, username, name, group, href, and link.
if (!empty($context['users_online']))
{
	echo '
			', sprintf($txt['users_active'], $modSettings['lastActive']), ':<br />', implode(', ', $context['list_users_online']);

	// Showing membergroups?
	if (!empty($settings['show_group_key']) && !empty($context['membergroups']))
		echo '
			<br />[' . implode(']&nbsp;&nbsp;[', $context['membergroups']) . ']';
}

echo '
		</p>
		<p class="last smalltext">
			', $txt['most_online_today'], ': <strong>', comma_format($modSettings['mostOnlineToday']), '</strong>.
			', $txt['most_online_ever'], ': ', comma_format($modSettings['mostOnline']), ' (', timeformat($modSettings['mostDate']), ')
		</p>';

