operation.search:
// Display user name and time.
echo '
	<ul id="greeting_section" class="titlebg2">
		<li id="time" class="smalltext floatright">
			' , $context['current_time'], '
			<a href="#" onclick="mainHeader.toggle(); return false;"><img id="upshrink" src="', $settings['images_url'], '/', empty($options['collapse_header']) ? 'upshrink.gif' : 'upshrink2.gif', '" alt="*" title="', $txt['upshrink_description'], '" align="bottom" /></a>
		</li>';

if ($context['user']['is_logged'])
	echo '
		<li id="name">', $txt['hello_member_ndt'], ' <em>', $context['user']['name'], '</em></li>';
else
	echo '
		<li id="name">', $txt['hello_guest'], ' <em>', $txt['guest'], '</em></li>';

echo '
	</ul>

operation.add:
// Display user name and time.
echo '
	<ul id="greeting_section" class="titlebg2">
		<li id="time" class="floatright"><strong>
			' , $context['current_time'], '
			</strong><a href="#" onclick="mainHeader.toggle(); return false;"><img id="upshrink" src="', $settings['images_url'], '/', empty($options['collapse_header']) ? 'upshrink.gif' : 'upshrink2.gif', '" alt="*" title="', $txt['upshrink_description'], '" align="bottom" /></a>
		</li>';

if ($context['user']['is_logged'])
	echo '
		<li id="name"><strong>', $txt['hello_member_ndt'],'<em> ', $context['user']['name'],'</em>, ', $txt['bksmf_welcome_back'],' <em>', $context['forum_name'],'</em>.</strong></li>';
else
	echo '
		<li id="name"><strong>', $txt['bksmf_hello'],'<em> ', $txt['guest'],'</em>, ', $txt['bksmf_welcome_to'],' <em>', $context['forum_name'],'</em>.</strong></li>';

echo '
	</ul>

//BK-SMF Welcome Message Mod 
$txt['bksmf_welcome_back'] = 'Welcome back to';
$txt['bksmf_hello'] = 'Hello';
$txt['bksmf_welcome_to'] = 'Welcome to';

